import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for upload modal to appear and take screenshot
    await page.waitForSelector('input[placeholder*="hashtag"]', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Find caption input using multiple selector strategies
    const captionSelector = 'input[placeholder*="Add a caption"], textarea[placeholder*="Add a caption"], #videoCaption';
    await page.waitForSelector(captionSelector);

    // Type caption text
    await page.fill(captionSelector, 'Amazing video!');
    await page.waitForTimeout(500);

    // Verify text was entered and take final screenshot
    const enteredText = await page.$eval(captionSelector, el => el.value);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_entered.png'));

    return {
      success: true,
      action: "Entered 'Amazing video!' in caption field of upload modal",
      extractedData: {
        captionText: enteredText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
