import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id='1'
    await page.waitForSelector('[data-video-id="1"]', { timeout: 5000 });

    // Wait for the like button within the first video container
    const likeButton = await page.waitForSelector('[data-video-id="1"] .like-btn', { timeout: 3000 });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_like.png'));

    // Click the like button using JavaScript click to ensure it triggers
    await likeButton.evaluate(btn => btn.click());

    // Wait for state change (check if class changes to indicate liked state)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_like.png'));

    // Verify the like action was successful by checking button state
    const isLiked = await page.evaluate(() => {
      const likeBtn = document.querySelector('[data-video-id="1"] .like-btn');
      return likeBtn.classList.contains('active') || 
             likeBtn.style.color === 'red' || 
             likeBtn.querySelector('i').classList.contains('fas');
    });

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1') and changed to liked state",
      extractedData: {
        videoId: 1,
        isLiked: isLiked,
        likeButtonText: await page.textContent('[data-video-id="1"] .like-btn')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' to load
    await page.waitForSelector('div.video-container[data-video-id="1"]', { timeout: 5000 });

    // Find the like button within the first video container using multiple selector strategies
    const likeButton = await page.$('div.video-container[data-video-id="1"] button.like-btn');

    if (!likeButton) {
      throw new Error('Like button not found in the first video container');
    }

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the like button
    await likeButton.click();

    // Wait for potential state changes
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract data to verify the action was successful
    const likeStatus = await page.$eval(
      'div.video-container[data-video-id="1"] button.like-btn',
      (btn) => btn.classList.contains('liked') || btn.getAttribute('aria-pressed')
    );

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        liked: likeStatus,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
