import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' and its play button
    await page.waitForSelector('div.video-container[data-video-id="1"]');
    await page.waitForSelector('div.video-container[data-video-id="1"] button.play-pause-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button using multiple selector strategies
    await page.click('div.video-container[data-video-id="1"] button.play-pause-btn');

    // Wait for state change and take screenshot after clicking
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract button state to verify action
    const buttonState = await page.$eval(
      'div.video-container[data-video-id="1"] button.play-pause-btn',
      btn => btn.innerHTML
    );

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        buttonIcon: buttonState,
        videoId: '1',
        buttonClass: 'play-pause-btn'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click first video's play/pause button",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id='1'
    await page.waitForSelector('div[data-video-id="1"]', { timeout: 5000 });

    // Find the play/pause button within the first video container
    const playPauseBtn = await page.$('div[data-video-id="1"] button.play-pause-btn');

    if (!playPauseBtn) {
      throw new Error('Play/Pause button not found in the first video container');
    }

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await playPauseBtn.click();

    // Wait for any UI changes after click
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract video state to verify the action
    const videoState = await page.evaluate(() => {
      const video = document.querySelector('div[data-video-id="1"] video');
      return {
        isPaused: video ? video.paused : null,
        currentTime: video ? video.currentTime : null
      };
    });

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        videoState: videoState,
        buttonClicked: "First video's play/pause button"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });
    screenshots.push(errorScreenshot);

    return {
      success: false,
      action: "Failed to click first video's play/pause button",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('[data-video-id="1"]', { timeout: 5000 });

    // Wait for play/pause button within the container
    const playPauseBtn = await videoContainer.waitForSelector('.play-pause-btn', { timeout: 5000 });

    // Take screenshot before interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await playPauseBtn.click();

    // Wait for UI to update
    await page.waitForTimeout(1000);

    // Take screenshot after interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract button state information
    const buttonState = await playPauseBtn.$eval('i', el => el.className);
    const isPaused = buttonState.includes('fa-play'); // If icon changed to play, video is paused

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        buttonState: buttonState,
        isVideoPaused: isPaused
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
