import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container and hover to reveal controls
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');
    await videoContainer.hover();

    // Wait for volume controls to be visible
    await page.waitForSelector('div[data-video-id="1"] input.volume-slider', { state: 'visible' });

    // Set volume slider to 50% (0.5)
    await page.$eval('div[data-video-id="1"] input.volume-slider', slider => {
        slider.value = 0.5;
        slider.dispatchEvent(new Event('input', { bubbles: true }));
    });

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume was set correctly
    const volumeValue = await page.$eval('div[data-video-id="1"] input.volume-slider', el => el.value);

    return {
      success: true,
      action: "Set first video volume slider to 50%",
      extractedData: {
        videoId: "1",
        volumeSetTo: volumeValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to set volume slider",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

