import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container and hover to reveal controls
    const firstVideoContainer = await page.waitForSelector('div[data-video-id="1"]');
    await firstVideoContainer.hover();

    // Wait for volume controls to appear and click volume button to reveal slider
    const volumeButton = await page.waitForSelector('div[data-video-id="1"] button.volume-btn');
    await volumeButton.click();

    // Wait for volume slider to be visible and set value to 50%
    const volumeSlider = await page.waitForSelector('div[data-video-id="1"] input.volume-slider');
    await volumeSlider.evaluate((el, value) => {
        el.value = value;
        el.dispatchEvent(new Event('input', { bubbles: true }));
        el.dispatchEvent(new Event('change', { bubbles: true }));
    }, 0.5);

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    // Verify the volume was set correctly
    const currentVolume = await volumeSlider.evaluate(el => el.value);
    const extractedData = {
        videoId: '1',
        volumeSet: currentVolume,
        success: parseFloat(currentVolume) === 0.5
    };

    return {
      success: true,
      action: "Set first video volume slider to 50%",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
