import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the upload button
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');

    // Wait for upload modal to appear (checking for file input field)
    await page.waitForSelector('#videoFileInput', { state: 'visible' });

    // Take screenshot after opening upload modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal_open.png'));

    // Extract modal information
    const modalInfo = await page.evaluate(() => {
      const fileInput = document.querySelector('#videoFileInput');
      const hashtagInput = document.querySelector('#videoHashtags');
      const privacySelect = document.querySelector('#videoPrivacy');

      return {
        fileInputVisible: fileInput ? fileInput.offsetParent !== null : false,
        hashtagPlaceholder: hashtagInput ? hashtagInput.placeholder : null,
        privacyOptions: privacySelect ? Array.from(privacySelect.options).map(opt => opt.text) : []
      };
    });

    return {
      success: true,
      action: "Clicked upload button and opened upload modal",
      extractedData: {
        modalVisible: true,
        ...modalInfo
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open upload modal",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

