import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the upload button using multiple selector strategies
    await page.waitForSelector('#uploadBtn', { state: 'visible' });
    await page.click('#uploadBtn');

    // Wait for upload modal to appear using alternative selectors
    await page.waitForSelector('#videoFileInput', { state: 'visible', timeout: 5000 });

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract modal data to verify successful opening
    const modalVisible = await page.isVisible('#videoFileInput');
    const modalTitle = await page.$eval('.upload-modal h2', el => el.textContent) || 'Upload Modal';

    return {
      success: true,
      action: "Clicked upload button and opened upload modal",
      extractedData: {
        modalOpened: modalVisible,
        modalTitle: modalTitle.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
