import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    await page.waitForSelector('div[data-video-id="1"]');

    // Hover over video container to reveal controls
    const videoContainer = page.locator('div[data-video-id="1"]');
    await videoContainer.hover();

    // Wait for volume slider to be visible
    await page.waitForSelector('div[data-video-id="1"] input.volume-slider', { state: 'visible' });

    // Set volume slider value to 0.5 (50%)
    const volumeSlider = page.locator('div[data-video-id="1"] input.volume-slider');
    await volumeSlider.fill('0.5');

    // Verify the volume was set correctly
    const volumeValue = await volumeSlider.inputValue();

    // Take screenshot after setting volume
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set first video's volume slider to 50%",
      extractedData: {
        videoId: '1',
        volumeValue: volumeValue,
        success: volumeValue === '0.5'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to set video volume",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

