import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container
    const firstVideoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Hover over the video container to reveal controls
    await firstVideoContainer.hover();
    await page.waitForTimeout(500); // Brief pause for UI animation

    // Take screenshot after hover
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_hovered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_hovered.png'));

    // Find and set volume slider using multiple selector strategies
    const volumeSlider = await page.waitForSelector('div[data-video-id="1"] input.volume-slider');

    // Set volume to 50% using direct value assignment
    await page.evaluate((slider) => {
      slider.value = 0.5;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
      slider.dispatchEvent(new Event('change', { bubbles: true }));
    }, volumeSlider);

    // Verify the volume was set correctly
    const currentVolume = await page.evaluate((slider) => slider.value, volumeSlider);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_volume_set.png'));

    return {
      success: true,
      action: "Set first video volume slider to 50%",
      extractedData: {
        videoId: 1,
        volumeSet: currentVolume,
        success: Math.abs(currentVolume - 0.5) < 0.01 // Check if volume is approximately 0.5
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
