import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the fullscreen button of the first video
    await page.waitForSelector('div.video-container[data-video-id="1"]');
    await page.waitForSelector('div.video-container[data-video-id="1"] button.fullscreen-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_fullscreen.png'));

    // Click the fullscreen button using multiple selector strategies
    await page.click('div.video-container[data-video-id="1"] button.fullscreen-btn');

    // Wait for potential fullscreen transition and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_fullscreen.png'));

    // Extract video information to verify task completion
    const videoInfo = await page.evaluate(() => {
      const container = document.querySelector('div.video-container[data-video-id="1"]');
      const video = container.querySelector('video');
      return {
        videoId: container.getAttribute('data-video-id'),
        videoSrc: video ? video.src : null,
        isFullscreen: document.fullscreenElement !== null
      };
    });

    return {
      success: true,
      action: "Clicked fullscreen button for first video (data-video-id='1')",
      extractedData: videoInfo,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

