    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to load
    await page.waitForSelector('div[data-video-id="1"]', { timeout: 5000 });

    // Use multiple selector strategies for the fullscreen button
    const fullscreenBtn = await page.$('div[data-video-id="1"] button.fullscreen-btn');

    if (!fullscreenBtn) {
      throw new Error('Fullscreen button not found for video 1');
    }

    // Click the fullscreen button
    await fullscreenBtn.click();

    // Wait for potential fullscreen transition
    await page.waitForTimeout(1000);

    // Take screenshot after clicking fullscreen
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen.png'));

    // Verify action by checking if video container has fullscreen-related attributes
    const videoContainer = await page.$('div[data-video-id="1"]');
    const videoClasses = await videoContainer.getAttribute('class');
    const isFullscreen = videoClasses.includes('fullscreen') || await page.evaluate(() => 
      document.fullscreenElement !== null
    );

    return {
      success: true,
      action: "Clicked fullscreen button for first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        isFullscreen: isFullscreen,
        buttonClasses: await fullscreenBtn.getAttribute('class')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to switch first video to fullscreen",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('[data-video-id="1"]');

    // Hover over the video container to make controls visible
    await videoContainer.hover();

    // Wait for fullscreen button to be visible and click it
    const fullscreenBtn = await videoContainer.waitForSelector('.fullscreen-btn');
    await fullscreenBtn.click();

    // Wait for fullscreen mode to activate
    await page.waitForTimeout(1000);

    // Take screenshot after fullscreen activation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_fullscreen.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_fullscreen.png'));

    return {
      success: true,
      action: "Successfully clicked fullscreen button on first video (data-video-id='1')",
      extractedData: {
        videoId: '1',
        fullscreenStatus: 'activated'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to switch first video to fullscreen mode",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
