import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Following page
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');

    // Wait for following page to load and take screenshot
    await page.waitForSelector('.following-user', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Extract user profiles data
    const userProfiles = await page.$$eval('.following-user', (cards) => {
      return cards.map(card => {
        const username = card.querySelector('.username')?.textContent?.trim() || 
                         card.querySelector('h3')?.textContent?.trim() || 
                         'N/A';

        const followers = card.querySelector('.follower-count')?.textContent?.trim() || 
                         card.querySelector('.followers')?.textContent?.trim() || 
                         'N/A';

        return { username, followers };
      });
    });

    // Take final screenshot with results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_results.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards in Following page",
      extractedData: userProfiles,
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to extract user profile data from Following page",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

