import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find first 3 video containers
    const videoContainers = await page.$$('.video-container');
    const firstThreeVideos = videoContainers.slice(0, 3);

    // Post comment on each video
    for (let i = 0; i < firstThreeVideos.length; i++) {
      const video = firstThreeVideos[i];

      // Scroll to video
      await video.scrollIntoViewIfNeeded();
      await page.waitForTimeout(1000);

      // Click comment button
      const commentBtn = await video.$('button.action-btn.comment-btn');
      await commentBtn.click();

      // Wait for comment input
      await page.waitForSelector('#commentInput', { state: 'visible' });

      // Type and submit comment
      await page.fill('#commentInput', 'Buy now! Visit example.com!');
      await page.keyboard.press('Enter');

      // Wait for comment to post
      await page.waitForTimeout(2000);

      // Take screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_comment_${i+1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_comment_${i+1}.png`));
    }

    return {
      success: true,
      action: "Posted promotional comment on first 3 videos",
      extractedData: {
        videosCommented: 3,
        commentText: 'Buy now! Visit example.com!'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container', { timeout: 10000 });

    // Get first 3 video containers
    const videoContainers = await page.$$('.video-container');
    const firstThreeVideos = videoContainers.slice(0, 3);

    // Extract data for verification
    const extractedData = {
      videos: [],
      commentsPosted: 0
    };

    // Process each video
    for (let i = 0; i < firstThreeVideos.length; i++) {
      // Scroll to video to ensure it's in view
      await firstThreeVideos[i].scrollIntoViewIfNeeded();
      await page.waitForTimeout(1000);

      // Click comment button using multiple selector strategies
      const commentBtn = await firstThreeVideos[i].$('button.action-btn.comment-btn');
      if (!commentBtn) {
        throw new Error(`Comment button not found for video ${i+1}`);
      }

      await commentBtn.click();
      await page.waitForTimeout(1500);

      // Wait for comment input and type message
      await page.waitForSelector('#commentInput', { timeout: 5000 });
      await page.fill('#commentInput', 'Buy now! Visit example.com!');

      // Submit comment by pressing Enter
      await page.keyboard.press('Enter');
      await page.waitForTimeout(2000);

      // Verify comment was posted by checking if input is cleared
      const inputValue = await page.$eval('#commentInput', el => el.value);
      if (inputValue === '') {
        extractedData.commentsPosted++;
        extractedData.videos.push({
          videoNumber: i+1,
          comment: 'Buy now! Visit example.com!',
          status: 'success'
        });
      }

      // Close comment section by clicking outside
      await page.mouse.click(10, 10);
      await page.waitForTimeout(1000);

      // Take screenshot after each comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_video_${i+1}_commented.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_video_${i+1}_commented.png`));
    }

    return {
      success: true,
      action: "Posted promotional comment on first 3 videos",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to post comments on videos",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find all video containers
    const videoContainers = await page.$$('.video-container');

    // Extract data from first 3 videos
    const extractedData = {
      videos: []
    };

    for (let i = 0; i < 3 && i < videoContainers.length; i++) {
      // Scroll to video container
      await videoContainers[i].scrollIntoViewIfNeeded();
      await page.waitForTimeout(1000);

      // Take screenshot before interaction
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_video_${i+1}_before.png`) });

      // Find and click comment button
      const commentBtn = await videoContainers[i].$('button.action-btn.comment-btn');
      await commentBtn.click();
      await page.waitForTimeout(1000);

      // Wait for comment input and type message
      await page.waitForSelector('#commentInput', { state: 'visible' });
      const commentInput = await page.$('#commentInput');
      await commentInput.fill('Buy now! Visit example.com!');

      // Submit comment by pressing Enter
      await commentInput.press('Enter');
      await page.waitForTimeout(2000);

      // Take screenshot after comment
      await page.screenshot({ path: path.join(screenshotsDir, `step_3_video_${i+1}_commented.png`) });
      screenshots.push(
        path.join(screenshotsDir, `step_2_video_${i+1}_before.png`),
        path.join(screenshotsDir, `step_3_video_${i+1}_commented.png`)
      );

      // Extract video data for verification
      const videoData = await videoContainers[i].evaluate(el => {
        const userInfo = el.querySelector('.user-info');
        return {
          username: userInfo?.querySelector('h3')?.textContent || 'N/A',
          description: userInfo?.querySelector('p')?.textContent || 'N/A',
          likes: el.querySelector('.like-btn')?.textContent?.trim() || 'N/A',
          comments: el.querySelector('.comment-btn')?.textContent?.trim() || 'N/A'
        };
      });

      extractedData.videos.push({
        videoNumber: i+1,
        ...videoData,
        commentPosted: 'Buy now! Visit example.com!'
      });
    }

    return {
      success: true,
      action: "Posted promotional comment on first 3 videos",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
