import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Following page
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');

    // Wait for Following page to load and take screenshot
    await page.waitForSelector('.following-tab-btn', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Click Users tab
    await page.waitForSelector('.following-tab-btn[data-tab="users"]');
    await page.click('.following-tab-btn[data-tab="users"]');

    // Wait for tab switch and take final screenshot
    await page.waitForTimeout(1000); // Wait for UI update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify tab switch by checking active state
    const isUsersTabActive = await page.$eval('.following-tab-btn[data-tab="users"]', 
      el => el.classList.contains('active') || el.getAttribute('aria-selected') === 'true');

    return {
      success: true,
      action: "Switched to Users tab on Following page",
      extractedData: {
        usersTabActive: isUsersTabActive,
        currentTab: 'users'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

