import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('.video-container[data-video-id="1"]');

    // Hover over the video container to reveal controls
    await videoContainer.hover();

    // Wait for and locate the volume slider within the container
    const volumeSlider = await videoContainer.waitForSelector('input.volume-slider');

    // Get the bounding box of the slider for drag simulation
    const sliderBoundingBox = await volumeSlider.boundingBox();

    // Calculate drag coordinates for 50% position
    const targetX = sliderBoundingBox.x + (sliderBoundingBox.width * 0.5);
    const targetY = sliderBoundingBox.y + (sliderBoundingBox.height / 2);

    // Simulate drag action to set volume to 50%
    await page.mouse.move(sliderBoundingBox.x, targetY);
    await page.mouse.down();
    await page.mouse.move(targetX, targetY);
    await page.mouse.up();

    // Take screenshot after volume adjustment
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_adjusted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_adjusted.png'));

    // Verify the volume was set correctly
    const currentValue = await volumeSlider.evaluate(el => el.value);
    const extractedData = { volumeValue: currentValue };

    return {
      success: true,
      action: "Set first video's volume slider to 50%",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

