import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Hover over the video container to reveal controls
    await videoContainer.hover();

    // Wait for volume controls to be visible
    await page.waitForSelector('div[data-video-id="1"] input.volume-slider', { state: 'visible' });

    // Set volume slider value to 0.5 (50%) using JavaScript evaluation
    await page.$eval('div[data-video-id="1"] input.volume-slider', (slider, value) => {
      slider.value = value;
      slider.dispatchEvent(new Event('input', { bubbles: true }));
      slider.dispatchEvent(new Event('change', { bubbles: true }));
    }, 0.5);

    // Verify the volume was set correctly
    const volumeValue = await page.$eval('div[data-video-id="1"] input.volume-slider', el => el.value);

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_volume_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_volume_set.png'));

    return {
      success: true,
      action: "Set first video's volume slider to 50%",
      extractedData: {
        videoId: 1,
        volumeSet: volumeValue,
        success: parseFloat(volumeValue) === 0.5
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
