  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container and comment button
    await page.waitForSelector('div[data-video-id="1"]');
    await page.waitForSelector('div[data-video-id="1"] button.comment-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the comment button of the first video
    await page.click('div[data-video-id="1"] button.comment-btn');

    // Wait for comment modal to appear
    await page.waitForSelector('#commentInput');

    // Take screenshot after opening comment modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_modal_open.png'));

    // Extract comment count from the button
    const commentCount = await page.textContent('div[data-video-id="1"] button.comment-btn');
    const placeholderText = await page.getAttribute('#commentInput', 'placeholder');

    return {
      success: true,
      action: "Clicked comment button of first video and opened comment modal",
      extractedData: {
        commentCount: commentCount.trim(),
        commentPlaceholder: placeholderText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button of the first video
    await page.waitForSelector('div.video-container[data-video-id="1"]');
    await page.waitForSelector('div.video-container[data-video-id="1"] button.comment-btn');

    // Scroll to the video container to ensure it's in view
    await page.evaluate(() => {
      const videoContainer = document.querySelector('div.video-container[data-video-id="1"]');
      if (videoContainer) videoContainer.scrollIntoView();
    });

    // Click the comment button using multiple selector strategies
    await page.click('div.video-container[data-video-id="1"] button.comment-btn');

    // Wait for comment modal to appear
    await page.waitForTimeout(1000); // Brief wait for modal animation
    await page.waitForSelector('#commentInput', { timeout: 5000 });

    // Take screenshot after opening comments
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comments_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comments_open.png'));

    // Extract comment count for verification
    const commentCount = await page.$eval(
      'div.video-container[data-video-id="1"] button.comment-btn',
      el => el.textContent.trim()
    );

    return {
      success: true,
      action: "Successfully opened comment modal for first video",
      extractedData: {
        commentCount: commentCount,
        videoId: "1"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to open comment modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    await page.waitForSelector('div[data-video-id="1"]', { timeout: 5000 });

    // Wait for comment button to be visible
    await page.waitForSelector('div[data-video-id="1"] .comment-btn', { 
      state: 'visible',
      timeout: 5000 
    });

    // Click the comment button using multiple selector strategies
    await page.click('div[data-video-id="1"] .comment-btn');

    // Wait for comment modal to appear
    await page.waitForSelector('#commentInput', { 
      state: 'visible',
      timeout: 5000 
    });

    // Take screenshot after opening comment modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_open.png'));

    // Extract comment input placeholder to verify modal is open
    const commentPlaceholder = await page.$eval('#commentInput', el => el.placeholder);

    return {
      success: true,
      action: "Clicked first video's comment button and opened comment modal",
      extractedData: {
        commentInputPlaceholder: commentPlaceholder
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
