    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load
    await page.waitForSelector('.sidebar-item[data-page="profile"]');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the Profile menu item using multiple selector strategies
    await page.click('.sidebar-item[data-page="profile"]');

    // Wait for page transition
    await page.waitForTimeout(1000);

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_navigation.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_navigation.png'));

    // Verify navigation by checking if profile page is active
    const profileActive = await page.$eval('.sidebar-item[data-page="profile"]', 
      el => el.classList.contains('active'));

    // Extract profile information if available
    const profileInfo = await page.evaluate(() => {
      const profileItem = document.querySelector('.sidebar-item[data-page="profile"]');
      return {
        text: profileItem?.querySelector('span')?.textContent || '',
        isActive: profileItem?.classList.contains('active') || false
      };
    });

    return {
      success: true,
      action: "Clicked Profile menu in sidebar and navigated to profile page",
      extractedData: {
        profileMenuText: profileInfo.text,
        profilePageActive: profileInfo.isActive,
        navigationSuccessful: profileActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

