import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to be visible
    await page.waitForSelector('.sidebar', { state: 'visible' });

    // Use multiple selector strategies to find the Profile menu
    // Strategy 1: data attribute selector
    const profileMenuItem = await page.$('.sidebar-item[data-page="profile"]');

    // Strategy 2: Text content selector as fallback
    if (!profileMenuItem) {
      await page.click('.sidebar-item:has-text("Profile")');
    } else {
      await profileMenuItem.click();
    }

    // Wait for profile page to load
    await page.waitForSelector('#profilePage', { state: 'visible' });

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Extract profile information to verify success
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.profile-username')?.textContent;
      const followers = document.querySelector('.followers-count')?.textContent;
      return { username, followers };
    });

    return {
      success: true,
      action: "Clicked Profile menu in sidebar and navigated to profile page",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
