import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' to load
    await page.waitForSelector('[data-video-id="1"]', { timeout: 5000 });

    // Wait for comment button inside the container
    await page.waitForSelector('[data-video-id="1"] .comment-btn', { timeout: 5000 });

    // Click the comment button
    await page.click('[data-video-id="1"] .comment-btn');

    // Wait for comment modal to appear (assuming it has an ID of commentModal)
    await page.waitForSelector('#commentModal', { timeout: 3000, state: 'visible' })
      .catch(() => console.log('Comment modal might not have specific ID, checking for comment input instead'));

    // Alternative check for comment input field
    await page.waitForSelector('#commentInput', { timeout: 3000, state: 'visible' });

    // Take screenshot after opening comments
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comments_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comments_opened.png'));

    return {
      success: true,
      action: "Successfully clicked comment button on first video and opened comment modal",
      extractedData: {
        videoId: '1',
        commentButtonText: await page.textContent('[data-video-id="1"] .comment-btn')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

