    // Wait for the first video container with data-video-id='1'
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Wait for the comment button to be visible
    const commentBtn = await page.waitForSelector('div.video-container[data-video-id="1"] button.comment-btn', {
      state: 'visible'
    });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the comment button
    await commentBtn.click();

    // Wait a moment for the modal to open
    await page.waitForTimeout(1000);

    // Take screenshot after clicking to show modal opened
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Verify the comment modal is open by checking for comment input
    const commentInput = await page.$('#commentInput');
    const isModalOpen = commentInput !== null;

    return {
      success: true,
      action: "Clicked comment button on first video (data-video-id='1') to open comment modal",
      extractedData: {
        modalOpened: isModalOpen,
        videoId: '1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button of the first video
    await page.waitForSelector('[data-video-id="1"]');
    const commentBtn = await page.$('[data-video-id="1"] button.comment-btn');

    if (commentBtn) {
      await commentBtn.click();
      // Wait for comment modal to appear
      await page.waitForSelector('#commentInput', { timeout: 5000 });

      // Take screenshot after opening comments
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comments_open.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_comments_open.png'));

      // Extract comment input placeholder to verify modal opened
      const commentPlaceholder = await page.$eval('#commentInput', el => el.placeholder);

      return {
        success: true,
        action: "Successfully opened comment modal for first video",
        extractedData: {
          commentInputPlaceholder: commentPlaceholder
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('Comment button not found for first video');
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to open comment modal for first video",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
