import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button to open comment modal
    await page.waitForSelector('button.action-btn.comment-btn');
    await page.click('button.action-btn.comment-btn');
    await page.waitForTimeout(1000); // Wait for modal animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal.png'));

    // Type comment in the input field
    await page.waitForSelector('#commentInput');
    await page.fill('#commentInput', 'Great content!');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click send button
    await page.waitForSelector('#commentSendBtn');
    await page.click('#commentSendBtn');
    await page.waitForTimeout(500); // Wait for send action
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_sent.png'));

    // Verify comment was posted by checking if input is cleared
    const commentInputValue = await page.$eval('#commentInput', el => el.value);
    const isCommentSent = commentInputValue === '';

    return {
      success: isCommentSent,
      action: "Typed 'Great content!' in comment input and sent it",
      extractedData: {
        commentInputCleared: isCommentSent,
        finalCommentValue: commentInputValue
      },
      screenshots,
      error: isCommentSent ? null : 'Comment input was not cleared after sending'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button using multiple selector strategies
    const commentButton = await page.waitForSelector('button.action-btn.comment-btn, [data-testid="comment-button"]', { timeout: 5000 });
    await commentButton.click();

    // Wait for comment modal to appear
    await page.waitForSelector('#commentInput, [placeholder="Add a comment..."], .comment-modal', { timeout: 5000 });

    // Take screenshot of comment modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal.png'));

    // Type comment using multiple selector strategies
    const commentInput = await page.waitForSelector('#commentInput, [placeholder="Add a comment..."]', { timeout: 5000 });
    await commentInput.fill('Great content!');

    // Wait for and click send button using multiple selector strategies
    const sendButton = await page.waitForSelector('#commentSendBtn, button:has-text("Send"), [aria-label="Send comment"]', { timeout: 5000 });
    await sendButton.click();

    // Wait for comment to be posted (check for success indicator)
    await page.waitForTimeout(1000); // Brief pause for UI update

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_posted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_posted.png'));

    // Verify comment was posted by checking if input is cleared or success message appears
    const inputValue = await page.$eval('#commentInput, [placeholder="Add a comment..."]', el => el.value);
    const commentPosted = inputValue === '';

    return {
      success: commentPosted,
      action: "Posted comment 'Great content!' in comment modal",
      extractedData: {
        commentText: 'Great content!',
        commentPosted: commentPosted
      },
      screenshots,
      error: commentPosted ? null : 'Comment may not have been posted successfully'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the comment button using multiple selector strategies
    const commentBtnSelector = 'button.action-btn.comment-btn';
    await page.waitForSelector(commentBtnSelector, { state: 'visible', timeout: 5000 });
    await page.click(commentBtnSelector);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_opened.png'));

    // Wait for comment input and type the comment
    const commentInputSelector = '#commentInput';
    await page.waitForSelector(commentInputSelector, { state: 'visible', timeout: 5000 });
    await page.fill(commentInputSelector, 'Great content!');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_comment_typed.png'));

    // Click the send button using XPath as alternative selector
    const sendButtonXPath = '//button[contains(@id, "commentSendBtn")]';
    await page.waitForSelector(sendButtonXPath, { state: 'visible', timeout: 5000 });
    await page.click(sendButtonXPath);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_comment_sent.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_comment_sent.png'));

    // Verify the comment was posted by checking if input is cleared
    const commentInputValue = await page.$eval(commentInputSelector, el => el.value);
    const isCommentSent = commentInputValue === '';

    return {
      success: true,
      action: "Successfully wrote and sent 'Great content!' comment in the comment modal",
      extractedData: {
        commentPosted: isCommentSent,
        finalInputValue: commentInputValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
