import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following sidebar item
    const followingSidebar = await page.waitForSelector('.sidebar-item[data-page="following"]');
    await followingSidebar.click();
    await page.waitForTimeout(1000); // Wait for content to load

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Wait for and click Users tab using multiple selector strategies
    const usersTabSelector = '.following-tab-btn[data-tab="users"]';
    await page.waitForSelector(usersTabSelector, { state: 'visible' });

    // Alternative selector using XPath as fallback
    const usersTabXPath = '//button[contains(@class, "following-tab-btn") and @data-tab="users"]';

    // Try multiple selector strategies
    try {
        await page.click(usersTabSelector);
    } catch (error) {
        console.log('Primary selector failed, trying XPath fallback');
        const usersTab = await page.waitForSelector(`xpath=${usersTabXPath}`);
        await usersTab.click();
    }

    await page.waitForTimeout(500); // Wait for tab transition
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify tab switch was successful by checking active state
    const activeTab = await page.$eval(usersTabSelector, el => el.classList.contains('active'));

    return {
      success: true,
      action: "Successfully navigated to Following page and switched to Users tab",
      extractedData: {
        usersTabActive: activeTab,
        currentTab: 'users'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}
