import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and type #dance
    await page.waitForSelector('#searchInput');
    await page.fill('#searchInput', '#dance');

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_typed.png'));

    // Click search button using multiple selector strategies
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for potential search results to load
    await page.waitForTimeout(1000);

    // Take final screenshot after search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_complete.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_complete.png'));

    // Extract search input value to verify completion
    const searchValue = await page.$eval('#searchInput', el => el.value);
    const extractedData = {
      searchTerm: searchValue,
      searchPerformed: searchValue === '#dance'
    };

    return {
      success: true,
      action: "Typed '#dance' in search input and clicked search button",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for search input and verify it's visible
    const searchInput = await page.waitForSelector('input[placeholder="Search accounts and videos"]', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input_found.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input_found.png'));

    // Clear input and type #dance
    await searchInput.click({ clickCount: 3 }); // Select existing text
    await searchInput.press('Backspace'); // Clear input
    await searchInput.type('#dance', { delay: 50 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_text_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_text_entered.png'));

    // Find and click search button using multiple selector strategies
    const searchButton = await page.waitForSelector('.search-btn, #searchBtn', { state: 'visible' });
    await searchButton.click();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_search_clicked.png'));

    // Wait for search results to load
    await page.waitForTimeout(1000); // Brief pause for UI update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_results.png'));

    // Verify search was successful by checking URL or page content
    const searchValue = await page.$eval('#searchInput', el => el.value);
    const extractedData = { searchQuery: searchValue };

    return {
      success: true,
      action: "Successfully entered '#dance' in search and clicked search button",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete search task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

