import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover in sidebar
    await page.waitForSelector('.sidebar-item[data-page="discover"]');
    await page.click('.sidebar-item[data-page="discover"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Wait for trending hashtags and click the first one
    await page.waitForSelector('.hashtag.trending');
    const firstHashtag = await page.$('.hashtag.trending');
    const hashtagText = await firstHashtag.evaluate(el => el.textContent);
    await firstHashtag.click();
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked the first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Discover page in sidebar
    const discoverSelector = '.sidebar-item[data-page="discover"]';
    await page.waitForSelector(discoverSelector);
    await page.click(discoverSelector);

    // Wait for Discover page to load and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Wait for trending hashtags to load using multiple selector strategies
    const hashtagSelector = '.hashtag.trending';
    await page.waitForSelector(hashtagSelector, { timeout: 5000 });

    // Get first trending hashtag using different selector approach
    const firstHashtag = await page.$(`${hashtagSelector}:first-child`);
    if (!firstHashtag) {
      throw new Error('No trending hashtags found');
    }

    // Extract hashtag text for verification
    const hashtagText = await firstHashtag.evaluate(el => el.textContent.trim());

    // Click the first trending hashtag
    await firstHashtag.click();

    // Wait for navigation/update and take final screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText,
        clickedElement: 'First .hashtag.trending element'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

