import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following tab in sidebar
    await page.waitForSelector('[data-page="following"]');
    await page.click('[data-page="following"]');
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_tab.png'));

    // Wait for user profile cards to load
    await page.waitForSelector('.following-user');

    // Extract all user profile data
    const userData = await page.$$eval('.following-user', (userCards) => {
      return userCards.map(card => {
        const username = card.querySelector('.username')?.textContent?.trim() || 'N/A';
        const followers = card.querySelector('.followers')?.textContent?.trim() || 'N/A';
        return { username, followers };
      });
    });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_user_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_user_data_extracted.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards",
      extractedData: { users: userData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

