import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following page in sidebar
    await page.click('div.sidebar-item:has-text("Following")');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Wait for user profile cards to load
    await page.waitForSelector('.following-user', { timeout: 5000 });

    // Extract all user profile cards
    const userCards = await page.$$('.following-user');
    const extractedData = [];

    // Iterate through each user card and extract data
    for (let i = 0; i < userCards.length; i++) {
      const card = userCards[i];

      // Extract username using multiple selector strategies
      const username = await card.$eval('h3, .username, [class*="user-name"]', el => el.textContent.trim());

      // Extract follower count using multiple selector strategies
      const followerCount = await card.$eval('p:has-text("followers"), .follower-count, [class*="follower"]', el => el.textContent.trim());

      extractedData.push({
        username,
        followerCount
      });
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards on Following page",
      extractedData: extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract user data from Following page",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
