import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Following in sidebar
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');
    await page.waitForTimeout(1000); // Wait for page transition
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Click Users tab
    await page.waitForSelector('.following-tab-btn[data-tab="users"]');
    await page.click('.following-tab-btn[data-tab="users"]');
    await page.waitForTimeout(1000); // Wait for tab transition
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify tab is active
    const isUsersTabActive = await page.$eval('.following-tab-btn[data-tab="users"]', 
      el => el.classList.contains('active'));

    return {
      success: true,
      action: "Switched to Users tab in Following page",
      extractedData: {
        usersTabActive: isUsersTabActive,
        currentTab: "users"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to switch to Users tab in Following page",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

