import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following sidebar item to navigate to Following page
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');
    await page.waitForLoadState('networkidle');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Wait for and click on Users tab using the specific selector
    await page.waitForSelector('.following-tab-btn[data-tab="users"]', { timeout: 5000 });
    await page.click('.following-tab-btn[data-tab="users"]');

    // Wait for tab transition to complete
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify tab switch was successful
    const isActive = await page.$eval('.following-tab-btn[data-tab="users"]', 
      el => el.classList.contains('active'));

    const extractedData = {
      tabStatus: isActive ? 'active' : 'inactive',
      tabText: await page.$eval('.following-tab-btn[data-tab="users"]', el => el.textContent.trim())
    };

    return {
      success: true,
      action: "Successfully navigated to Following page and switched to Users tab",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
