import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container', { timeout: 5000 });

    // Get all video containers
    const videoContainers = await page.$$('.video-container');

    // Extract data from each container
    const videoData = [];

    for (const container of videoContainers) {
      try {
        // Get video ID from data attribute
        const videoId = await container.getAttribute('data-video-id');

        // Get like count
        const likeCount = await container.$eval('button.like-btn span', el => el.textContent.trim());

        // Get comment count
        const commentCount = await container.$eval('button.comment-btn span', el => el.textContent.trim());

        videoData.push({
          videoId,
          likeCount,
          commentCount
        });
      } catch (error) {
        console.log(`Error extracting data from one container: ${error.message}`);
      }
    }

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video-id from all video containers",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
