import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for sidebar to load
    await page.waitForSelector('.sidebar');

    // Click on Profile menu using data-page attribute selector
    await page.waitForSelector('.sidebar-item[data-page="profile"]');
    await page.click('.sidebar-item[data-page="profile"]');

    // Wait for profile page to load (assuming it becomes active)
    await page.waitForTimeout(1000); // Brief pause for UI update

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_page.png'));

    // Verify navigation by checking if Profile menu is now active
    const isProfileActive = await page.$eval('.sidebar-item[data-page="profile"]', 
        el => el.classList.contains('active'));

    return {
      success: true,
      action: "Clicked Profile menu in sidebar and navigated to profile page",
      extractedData: {
        profileMenuActive: isProfileActive,
        profileMenuText: await page.$eval('.sidebar-item[data-page="profile"] span', el => el.textContent.trim())
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

