import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following tab in sidebar
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');
    await page.waitForTimeout(1000);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Wait for user profile cards to load
    await page.waitForSelector('.following-user', { timeout: 5000 });

    // Extract username and follower data from all profile cards
    const extractedData = await page.$$eval('.following-user', (users) => {
      return users.map(user => {
        const username = user.querySelector('.username')?.innerText || 'N/A';
        const followerCount = user.querySelector('.follower-count')?.innerText || 'N/A';
        return { username, followerCount };
      });
    });

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_extracted_data.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_extracted_data.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all following user profiles",
      extractedData: { users: extractedData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following tab in sidebar
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');

    // Wait for Following page to load and take screenshot
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Extract all user profile cards
    await page.waitForSelector('.following-user');
    const userCards = await page.$$('.following-user');

    const profiles = [];

    // Iterate through each user card and extract data
    for (let i = 0; i < userCards.length; i++) {
      const card = userCards[i];

      // Extract username
      const username = await card.$eval('.username', el => el.textContent.trim());

      // Extract follower count using multiple selector strategies
      let followerCount;

      // Try different selector strategies for robustness
      try {
        followerCount = await card.$eval('.follower-count', el => el.textContent.trim());
      } catch (e) {
        try {
          followerCount = await card.$eval('.stats span:last-child', el => el.textContent.trim());
        } catch (e) {
          followerCount = 'N/A';
        }
      }

      profiles.push({
        username,
        followerCount
      });
    }

    // Take final screenshot with results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_results.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards in the Following page",
      extractedData: { profiles },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

