import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Following page using sidebar
    await page.waitForSelector('.sidebar-item[data-page="following"]');
    await page.click('.sidebar-item[data-page="following"]');
    await page.waitForTimeout(1000); // Wait for page transition

    // Take screenshot after navigating to Following page
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Switch to Users tab using the provided selector
    await page.waitForSelector('.following-tab-btn[data-tab="users"]');
    await page.click('.following-tab-btn[data-tab="users"]');
    await page.waitForTimeout(1000); // Wait for tab switch

    // Take screenshot after switching to Users tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_users_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_users_tab.png'));

    // Verify the tab switch was successful
    const activeTab = await page.$eval('.following-tab-btn.active', el => el.getAttribute('data-tab'));
    const usersTabContent = await page.$eval('.users-tab-content', el => el.textContent.trim());

    return {
      success: true,
      action: "Switched to Users tab in Following page",
      extractedData: {
        activeTab: activeTab,
        tabContent: usersTabContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

