import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal_opened.png'));

    // Wait for caption input field and enter text
    await page.waitForSelector('#videoCaption');
    await page.fill('#videoCaption', 'Amazing video!');

    // Verify text was entered
    const captionValue = await page.$eval('#videoCaption', el => el.value);

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    return {
      success: true,
      action: "Entered 'Amazing video!' in caption field of upload modal",
      extractedData: {
        captionValue: captionValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

