import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for upload modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Input caption using multiple selector strategies
    const captionSelectors = [
      '#videoCaption',
      'input[placeholder*="caption"]',
      'input[placeholder*="Caption"]',
      'textarea[placeholder*="caption"]',
      'textarea[placeholder*="Caption"]'
    ];

    let captionField = null;
    for (const selector of captionSelectors) {
      captionField = await page.$(selector);
      if (captionField) break;
    }

    if (!captionField) {
      throw new Error('Caption field not found');
    }

    // Clear and input caption text
    await captionField.fill('');
    await captionField.type('Amazing video!', { delay: 50 });

    // Verify input and take screenshot
    const enteredText = await captionField.inputValue();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    return {
      success: true,
      action: "Successfully entered 'Amazing video!' in caption field",
      extractedData: {
        enteredCaption: enteredText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}
