import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Following sidebar item using XPath
    const followingButton = await page.waitForSelector('//div[contains(@class, "sidebar-item") and contains(., "Following")]');
    await followingButton.click();
    await page.waitForTimeout(1000);

    // Wait for following page to load
    await page.waitForSelector('#followingPage', { state: 'visible' });

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_following_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_following_page.png'));

    // Extract all user profile cards
    const userCards = await page.$$('.following-user');
    const extractedData = [];

    for (const card of userCards) {
      // Extract username using multiple selector strategies
      const username = await card.$eval('.username, [class*="user-name"], h3, h4', el => el.textContent.trim());

      // Extract follower count using multiple selector strategies
      const followerCount = await card.$eval('.follower-count, [class*="follower"], [class*="followers"]', el => el.textContent.trim());

      extractedData.push({
        username,
        followers: followerCount
      });
    }

    // Take final screenshot with results
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_results.png'));

    return {
      success: true,
      action: "Extracted username and follower count from all user profile cards in Following page",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract user data from Following page",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
