import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video containers to load
    await page.waitForSelector('.video-container', { timeout: 10000 });

    // Extract data from all video containers
    const videoData = await page.$$eval('.video-container', (containers) => {
      return containers.map(container => {
        const videoId = container.getAttribute('data-video-id');

        // Try multiple selector strategies for like count
        const likeBtn = container.querySelector('button.like-btn');
        let likeCount = likeBtn?.querySelector('span')?.textContent || 
                       likeBtn?.textContent?.trim() || 
                       '0';

        // Try multiple selector strategies for comment count
        const commentBtn = container.querySelector('button.comment-btn');
        let commentCount = commentBtn?.querySelector('span')?.textContent || 
                          commentBtn?.textContent?.trim() || 
                          '0';

        // Clean numeric values (remove commas, K/M suffixes)
        likeCount = likeCount.replace(/[^0-9.]/g, '');
        commentCount = commentCount.replace(/[^0-9.]/g, '');

        return {
          'video-id': videoId,
          'like-count': likeCount,
          'comment-count': commentCount
        };
      });
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted like count, comment count, and video-id from all video containers",
      extractedData: videoData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract video data",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

