import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Wait for the play/pause button within the first video container
    const playPauseBtn = await videoContainer.waitForSelector('button.play-pause-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await playPauseBtn.click();

    // Wait for state change and take screenshot after click
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract verification data
    const buttonIcon = await playPauseBtn.$eval('i', el => el.className);
    const videoState = await videoContainer.$eval('video', el => el.paused ? 'paused' : 'playing');

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        buttonIcon,
        videoState,
        videoId: await videoContainer.getAttribute('data-video-id')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

