import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]', {
      timeout: 10000
    });

    // Wait for and click the play-pause button within the specific container
    const playPauseBtn = await videoContainer.waitForSelector('button.play-pause-btn');
    await playPauseBtn.click();

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Extract video status to verify the action
    const videoStatus = await page.evaluate(() => {
      const video = document.querySelector('div[data-video-id="1"] video');
      return {
        isPaused: video ? video.paused : null,
        currentTime: video ? video.currentTime : null
      };
    });

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        videoStatus,
        buttonClicked: "First video's play/pause button"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
