import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button to open modal
    await page.waitForSelector('#uploadBtn');
    await page.click('#uploadBtn');

    // Wait for upload modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_upload_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_upload_modal.png'));

    // Fill caption field using multiple selector strategies
    await page.waitForSelector('#videoCaption');
    await page.fill('#videoCaption', 'Amazing video!');

    // Verify input was successful
    const captionValue = await page.$eval('#videoCaption', el => el.value);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_filled.png'));

    return {
      success: true,
      action: "Successfully entered 'Amazing video!' in caption field",
      extractedData: {
        caption: captionValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
