    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to be visible
    await page.waitForSelector('.video-container');

    // Click the share button for the first video
    const shareBtn = await page.$('button.action-btn.share-btn');
    if (!shareBtn) throw new Error('Share button not found');
    await shareBtn.click();

    // Wait for share modal to appear
    await page.waitForTimeout(1000); // Wait for animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_share_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_share_modal.png'));

    // Find and click 'Copy Link' option using multiple selector strategies
    const copyLinkSelector = [
      'button:has-text("Copy Link")',
      '.share-option:has-text("Copy Link")',
      'text=Copy Link'
    ];

    let copyLinkBtn = null;
    for (const selector of copyLinkSelector) {
      copyLinkBtn = await page.$(selector);
      if (copyLinkBtn) break;
    }

    if (!copyLinkBtn) throw new Error('Copy Link button not found in share modal');
    await copyLinkBtn.click();

    // Wait for copy confirmation and take final screenshot
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_copy_link_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_copy_link_clicked.png'));

    return {
      success: true,
      action: "Successfully clicked 'Copy Link' in the first video's share modal",
      extractedData: {
        videoTitle: await page.$eval('.video-container .user-details p', el => el.textContent),
        shareCount: await page.$eval('.share-btn', el => el.textContent.trim())
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container to load
    await page.waitForSelector('.video-container');

    // Click the share button for the first video
    await page.waitForSelector('button.share-btn');
    const shareButton = await page.$('button.share-btn');
    await shareButton.click();

    // Wait for share modal to appear and take screenshot
    await page.waitForTimeout(1000); // Wait for animation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_share_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_share_modal.png'));

    // Click Copy Link option using multiple selector strategies
    // First try text content selector
    const copyLinkButton = await page.$('text=Copy Link');
    if (copyLinkButton) {
      await copyLinkButton.click();
    } else {
      // Fallback to XPath if text selector fails
      const copyLinkXPath = await page.$('//button[contains(text(), "Copy Link")]');
      if (copyLinkXPath) {
        await copyLinkXPath.click();
      } else {
        throw new Error('Copy Link button not found');
      }
    }

    // Take final screenshot
    await page.waitForTimeout(500); // Wait for action to complete
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_copy_link_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_copy_link_clicked.png'));

    return {
      success: true,
      action: "Clicked 'Copy Link' in the first video's share modal",
      extractedData: {
        videoTitle: await page.$eval('.video-container .user-details p', el => el.textContent),
        shareCount: await page.$eval('.share-btn', el => el.textContent.trim())
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
