import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to Discover page
    const discoverButton = await page.waitForSelector('div.sidebar-item[data-page="discover"]');
    await discoverButton.click();
    await page.waitForTimeout(1000); // Wait for page transition

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Wait for trending hashtags to load and click the first one
    const firstHashtag = await page.waitForSelector('.hashtag.trending:first-child');
    const hashtagText = await firstHashtag.textContent();
    await firstHashtag.click();
    await page.waitForTimeout(1000); // Wait for hashtag page to load

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

