import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover page in sidebar
    await page.waitForSelector('.sidebar-item[data-page="discover"]');
    await page.click('.sidebar-item[data-page="discover"]');
    await page.waitForTimeout(1000); // Allow page transition

    // Wait for trending hashtags to load
    await page.waitForSelector('.hashtag.trending', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Click first trending hashtag
    const firstHashtag = await page.$('.hashtag.trending');
    const hashtagText = await page.evaluate(el => el.textContent.trim(), firstHashtag);
    await firstHashtag.click();

    // Wait for content to load after click
    await page.waitForTimeout(2000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked first trending hashtag on Discover page",
      extractedData: { clickedHashtag: hashtagText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
