import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1'
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Wait for like button inside the first video container
    const likeBtn = await page.waitForSelector('div.video-container[data-video-id="1"] button.like-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_like.png'));

    // Click the like button
    await likeBtn.click();

    // Wait for potential state change (optional short delay for visual feedback)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_like.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_like.png'));

    // Verify the button state changed (check if it has active/liked class)
    const isLiked = await page.$eval(
      'div.video-container[data-video-id="1"] button.like-btn',
      btn => btn.classList.contains('active') || btn.classList.contains('liked')
    );

    return {
      success: true,
      action: "Clicked like button on first video (data-video-id='1')",
      extractedData: {
        videoId: '1',
        isLiked: isLiked,
        likeButtonText: await page.$eval(
          'div.video-container[data-video-id="1"] button.like-btn',
          btn => btn.textContent.trim()
        )
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

