import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id='1'
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Wait for and click the comment button in the first video container
    const commentButton = await page.waitForSelector('div.video-container[data-video-id="1"] button.action-btn.comment-btn');
    await commentButton.click();

    // Wait for comment modal to appear (check for comment input field)
    await page.waitForSelector('#commentInput', { state: 'visible' });

    // Take screenshot after opening comment modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_comment_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_comment_modal_open.png'));

    return {
      success: true,
      action: "Successfully clicked comment button on first video and opened comment modal",
      extractedData: {
        videoId: "1",
        commentInputVisible: true
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' to load
    await page.waitForSelector('[data-video-id="1"]', { timeout: 5000 });

    // Take screenshot after video container loads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_video_container_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_video_container_loaded.png'));

    // Click comment button using multiple selector strategies
    const commentButton = await page.$('[data-video-id="1"] .comment-btn');
    if (commentButton) {
      await commentButton.click();

      // Wait for comment modal to appear
      await page.waitForSelector('#commentInput', { timeout: 3000 });

      // Take screenshot after opening comment modal
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_comment_modal_opened.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_comment_modal_opened.png'));

      // Extract comment count for verification
      const commentCount = await page.$eval('[data-video-id="1"] .comment-btn', btn => 
        btn.textContent.trim().replace(/\D/g, '')
      );

      return {
        success: true,
        action: "Clicked comment button on first video and opened comment modal",
        extractedData: {
          commentCount: commentCount
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('Comment button not found');
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

