import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click upload button using multiple selector strategies
    const uploadBtn = await page.waitForSelector('#uploadBtn, button.nav-btn i.fa-plus', { timeout: 5000 });
    await uploadBtn.click();
    await page.waitForTimeout(1000);

    // Wait for upload modal to appear using different strategies
    await page.waitForSelector('.modal, [class*="upload"], [class*="modal"]', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Find caption input using multiple selector strategies
    const captionInput = await page.waitForSelector('#videoCaption, input[placeholder*="caption"], textarea[placeholder*="caption"], [name*="caption"]', { timeout: 5000 });

    // Clear existing text and type new caption
    await captionInput.click({ clickCount: 3 });
    await captionInput.press('Backspace');
    await captionInput.type('Amazing video!');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_caption_entered.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_caption_entered.png'));

    // Verify the text was entered correctly
    const enteredText = await captionInput.evaluate(el => el.value);
    console.log('Entered caption:', enteredText);

    return {
      success: true,
      action: "Entered 'Amazing video!' in the caption field of upload modal",
      extractedData: {
        caption: enteredText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}
