import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click Discover in sidebar
    const discoverSelector = '.sidebar-item[data-page="discover"]';
    await page.waitForSelector(discoverSelector);
    await page.click(discoverSelector);
    await page.waitForTimeout(1000);

    // Wait for discover page to load and take screenshot
    await page.waitForSelector('#discoverPage');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discover_page.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discover_page.png'));

    // Wait for trending hashtags to load
    await page.waitForSelector('.hashtag.trending', { timeout: 5000 });

    // Get first trending hashtag and click it
    const firstHashtag = await page.$('.hashtag.trending:first-child');
    if (!firstHashtag) {
      throw new Error('No trending hashtags found');
    }

    const hashtagText = await firstHashtag.textContent();
    await firstHashtag.click();
    await page.waitForTimeout(2000);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtag_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtag_clicked.png'));

    return {
      success: true,
      action: "Clicked first trending hashtag on Discover page",
      extractedData: {
        hashtag: hashtagText.trim(),
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
