import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first video container with data-video-id='1'
    const videoContainer = await page.waitForSelector('div[data-video-id="1"]');

    // Wait for the play/pause button within the container
    const playPauseBtn = await videoContainer.waitForSelector('button.play-pause-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button
    await playPauseBtn.click();

    // Wait for UI update
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract video state information
    const iconClass = await playPauseBtn.$eval('i', el => el.className);
    const isPaused = iconClass.includes('fa-play'); // If icon shows play, video is paused

    return {
      success: true,
      action: "Clicked play/pause button on first video (video ID: 1)",
      extractedData: {
        videoId: 1,
        videoState: isPaused ? 'paused' : 'playing',
        iconClass: iconClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
