    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' and its play/pause button
    const videoContainerSelector = 'div[data-video-id="1"]';
    const playPauseButtonSelector = `${videoContainerSelector} button.play-pause-btn`;

    await page.waitForSelector(videoContainerSelector, { state: 'visible' });
    await page.waitForSelector(playPauseButtonSelector, { state: 'visible' });

    // Take screenshot before interaction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the play/pause button using multiple selector strategies
    await page.click(playPauseButtonSelector);

    // Wait for UI to update and take screenshot after interaction
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract button state information
    const buttonState = await page.$eval(playPauseButtonSelector, btn => {
      const icon = btn.querySelector('i');
      return {
        iconClass: icon.className,
        isPaused: icon.className.includes('fa-play'),
        isPlaying: icon.className.includes('fa-pause')
      };
    });

    return {
      success: true,
      action: "Clicked the play/pause button of the first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        buttonState: buttonState,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click the play/pause button",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first video's play/pause button
    const videoContainer = await page.waitForSelector('[data-video-id="1"]');
    const playPauseBtn = await videoContainer.waitForSelector('.play-pause-btn');

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    await playPauseBtn.click();

    // Wait for state change and take screenshot after clicking
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Verify the action by checking if video state changed
    const isPlaying = await page.evaluate(() => {
      const video = document.querySelector('[data-video-id="1"] video');
      return !video.paused;
    });

    return {
      success: true,
      action: "Clicked play/pause button of first video (data-video-id='1')",
      extractedData: {
        videoStatus: isPlaying ? 'playing' : 'paused',
        videoId: '1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click play/pause button",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
