import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the bookmark button for the first video
    await page.waitForSelector('[data-video-id="1"]');
    const bookmarkBtn = await page.$('[data-video-id="1"] .bookmark-btn');

    if (bookmarkBtn) {
      await bookmarkBtn.click();
      await page.waitForTimeout(1000); // Wait for UI update

      // Take screenshot after clicking bookmark
      await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmarked.png') });
      screenshots.push(path.join(screenshotsDir, 'step_2_bookmarked.png'));

      return {
        success: true,
        action: "Bookmarked the first video with data-video-id='1'",
        extractedData: {
          videoId: "1",
          action: "bookmarked"
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('Bookmark button not found for the first video');
    }
  } catch (error) {
    return {
      success: false,
      action: "Failed to bookmark the first video",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for video container with data-video-id='1' to be visible
    await page.waitForSelector('div[data-video-id="1"]', { state: 'visible' });

    // Find and click the bookmark button using multiple selector strategies
    const bookmarkBtn = await page.$('div[data-video-id="1"] .bookmark-btn');
    if (bookmarkBtn) {
      await bookmarkBtn.click();
    } else {
      // Alternative strategy: Use XPath to find bookmark button
      const bookmarkXPath = '//div[@data-video-id="1"]//button[contains(@class, "bookmark-btn")]';
      await page.waitForSelector(`xpath=${bookmarkXPath}`);
      await page.click(`xpath=${bookmarkXPath}`);
    }

    // Take screenshot after clicking bookmark
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_bookmark.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_bookmark.png'));

    // Verify bookmark was added by checking for active state or confirmation
    const isBookmarked = await page.$eval(
      'div[data-video-id="1"] .bookmark-btn',
      btn => btn.classList.contains('active') || btn.getAttribute('aria-pressed') === 'true'
    ).catch(() => false);

    return {
      success: true,
      action: "Bookmarked the first video (data-video-id='1')",
      extractedData: {
        videoId: 1,
        bookmarked: isBookmarked
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to bookmark the first video",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the bookmark button for first video
    await page.waitForSelector('div.video-container[data-video-id="1"]');

    // Using XPath to find the bookmark button within the specific video container
    const bookmarkBtn = await page.waitForSelector('xpath=//div[@data-video-id="1"]//button[contains(@class, "bookmark-btn")]');
    await bookmarkBtn.click();

    // Wait for any UI changes/animations
    await page.waitForTimeout(1000);

    // Take screenshot after clicking bookmark
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_bookmarked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_bookmarked.png'));

    // Extract confirmation data
    const bookmarkState = await page.evaluate(() => {
      const btn = document.querySelector('div[data-video-id="1"] .bookmark-btn');
      return {
        isBookmarked: btn ? btn.classList.contains('active') : false,
        buttonText: btn ? btn.textContent.trim() : null
      };
    });

    return {
      success: true,
      action: "Clicked bookmark button for first video (data-video-id='1')",
      extractedData: {
        videoId: "1",
        isBookmarked: bookmarkState.isBookmarked,
        buttonState: bookmarkState.buttonText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
